updateData.functionBody:
var result = nMinus1Factorial * n;

if(n < desiredN) {
    //iterate
    var updateData = [];
    updateData.push(["n",n+1]);
    updateData.push(["nMinus1Factorial",result]);
    apogeeMessenger.compoundDataUpdate(updateData);
}
else if(n > desiredN) {
    //restart
    var updateData = [];
    updateData.push(["n",1]);
    updateData.push(["nMinus1Factorial",1]);
    apogeeMessenger.compoundDataUpdate(updateData);
}

return result;
