function _addCasesData(row,index,columnDefs,usEntry) {
    if(index == 0) return;  //header row
    if(row.length <= 1) return; //invalid row

    let entry = _lookupEntry(row,index,columnDefs,usEntry);

    //add cases data
    entry.casesData = dataDownload.getTimeSeriesData(row,columnDefs);
    entry.lat = parseFloat(row[columnDefs.LAT]);
    entry.lon = parseFloat(row[columnDefs.LON]);
}

function _addDeathsData(row,index,columnDefs,usEntry) {
    if(index == 0) return;  //header row
    if(row.length <= 1) return; //invalid row

    let entry = _lookupEntry(row,index,columnDefs,usEntry);
    if(!entry) throw new Error("Missing cases entry for deaths data");

    //add deaths data
    entry.deathsData = dataDownload.getTimeSeriesData(row,columnDefs);
    entry.population = parseInt(row[columnDefs.POPULATION]);
}

function _lookupEntry(row,index,columnDefs,usEntry) {

    let state = row[columnDefs.STATE];
    let county = row[columnDefs.COUNTY];

    let stateEntry = _getChildEntry(state,"state",usEntry);
    if(county) {
        return _getChildEntry(county,"county",stateEntry);
    }
    else {
        return stateEntry
    }
}

function _getChildEntry(childName,childType,parentEntry) {
    if(!parentEntry.regions) parentEntry.regions = {};

    let childEntry = parentEntry.regions[childName];
    if(!childEntry) {
        childEntry = {};
        childEntry.name = childName;
        childEntry.type = childType;
        parentEntry.regions[childName] = childEntry;
    }
    return childEntry;
}

