for(let stateName in usEntry.regions) {
    let stateEntry = usEntry.regions[stateName];

    //add the county entries to populate the state entries where needed.
    if(stateEntry.regions) {

        //deleted unwanted county entries
        for(let countyName in stateEntry.regions) {
            let countyEntry = stateEntry.regions[countyName];
            if((!countyEntry.lat)||(!countyEntry.population)) {
                deletedEntries.push(stateName + ", " + countyName);
                delete stateEntry.regions[countyName];
                continue;
            }
        }


        if(!stateEntry.population) {
            stateEntry.population = _sumChildValues(stateEntry,"population");
        }

        if(!stateEntry.lat) {
            let latSum = _sumChildValues(stateEntry,"lat");
            let lonSum = _sumChildValues(stateEntry,"lon");
            let childCount = apogeeutil.jsonObjectLength(stateEntry.regions);
            stateEntry.lat = latSum/childCount;
            stateEntry.lon = lonSum/childCount;
        }

        if(!stateEntry.casesData) {
            stateEntry.casesData = _sumChildArrays(stateEntry,"casesData");
            stateEntry.deathsData = _sumChildArrays(stateEntry,"deathsData");
        }
    }

    //this is a signal for an entry we don't want 
    if(!stateEntry.lat) {
        delete usEntry.regions[stateName];
        deletedEntries.push(stateName);
        continue;
    }
}

return usEntry;

