function _sumChildValues(stateEntry,propName) {
    let value = 0;
    for(let countyName in stateEntry.regions) {
        let countyEntry = stateEntry.regions[countyName];
        value += countyEntry[propName]
    }
    return value;
}

function _sumChildArrays(stateEntry,propName) {
    let targetArray;
    for(let countyName in stateEntry.regions) {
        let countyEntry = stateEntry.regions[countyName];
        let sourceArray = countyEntry[propName];
        if(!targetArray) {
            targetArray = apogeeutil.jsonCopy(sourceArray);
        }
        else {
            sourceArray.forEach( (value,index) => targetArray[index] += value);
        }
    }
    return targetArray;
}

