let rowArray = csvText.split("\n");

let gridData = rowArray.map( row => {
    //our parsing is simplified to handl this specific data rather than a general cvs file.
    //only one (or zero) fields have quotes, we will pull it out.
    let firstQuoteIndex = row.indexOf("\"");

    //no quoted strings
    if(firstQuoteIndex < 0) return row.split(",");

    let secondQuoteIndex = row.indexOf("\"",firstQuoteIndex+1);
    if(secondQuoteIndex < 0) throw new Error("Error: Only 1 delimited quote!");
    //make sure there are only two delimited
    let thirdQuoteIndex = row.indexOf("\"",secondQuoteIndex+1);
    if(thirdQuoteIndex > 0) throw new Error("Error: More than 2 delimited quotes!");


    //split up and parse single the quoted field differently from the rest of the row.
    let firstString = row.substr(0,firstQuoteIndex-1);
    let secondString = row.substr(firstQuoteIndex+1,secondQuoteIndex-firstQuoteIndex-1);
    let thirdString = row.substr(secondQuoteIndex+2);

    let firstArray = firstString.split(",")
    let secondArray = secondString
    let thirdArray = thirdString.split(",");

    return firstArray.concat(secondArray).concat(thirdArray);
})

return gridData;

