/** This function generates a color for the index based on the total count. */
function _getColor(index,totalCount) {
    let r = [0,255];
    let g = [0,0];
    let b = [255,0];

    //this function will interpolate a given color based on the index
    let pick = c => c[0] + (c[1]-c[0]) * index / totalCount;

    return "rgb(" + pick(r) + "," + pick(g) + "," + pick(b) + ")";  
}

