function _validateEntry(entry,onlyChildChecks,errors) {
    if(!onlyChildChecks) {
        if(!entry.population) _addError("Missing population",entry,errors);
        if(!entry.lat) _addError("Missing lat",entry,errors);
        if(!entry.lon) _addError("Missing lon",entry,errors);
        if(!entry.casesData) _addError("Missing cases",entry,errors);
        if(!entry.deathsData) _addError("Missing deaths",entry,errors);
    }

    if(entry.regions) {
        for(let childName in entry.regions) {
            let childEntry = entry.regions[childName];
            _validateEntry(childEntry,false,errors);
        }
    }
}

function _addError(msg,entry,errors) {
    errors.push( entry.type + " = " + entry.name + ": " + msg);
}
