    else return null;

    let state = row[columnDef.STATE];
    if((state)&&(state != "")) entry.regionPath.push(state);

    let county = row[columnDef.COUNTY];
    if((county)&&(county != "")) entry.regionPath.push(county);

    //get the data
    entry.data = _getRowTimeSeries(row,columnDef);

    //latitude and longitude - assume 0.0 is an invalid value
    let latString = row[columnDef.LAT];
    let lonString = row[columnDef.LON];
    if((latString != "")&&(lonString != "")) {
        let lat = parseFloat(latString);
        let lon = parseFloat(lonString);
        if((lat != 0.0)&&(lon != 0.0)) {
            entry.lat = lat;
            entry.lon = lon;
        }
    }

    //fips (census info) - us only
    if(columnDef.FIPS !== undefined) {
        entry.fips = row[fips];
    }

    return entry;
}

function _getRowTimeSeries(row,columnDef) {
