//this function adds the inEntry to the given region entry, where the regionPathIndex specifies which level of the region path is applicable
//e.g. country, state, county
function _addToEntry(regionOutEntry,inEntry,regionPathIndex) {
    if(!regionOutEntry.regions) regionOutEntry.regions = {};

    //lookup this child region in the parent out region
    let childRegionName = inEntry.regionPath[regionPathIndex];
    let childRegionOutEntry = regionOutEntry.regions[childRegionName];
    if(!childRegionOutEntry) {
        //create a new entry if needed
        childRegionOutEntry = {};
        childRegionOutEntry.name = childRegionName;
        childRegionOutEntry.type = inEntry.type;
        regionOutEntry.regions[childRegionName] = childRegionOutEntry;
    }

    //check if we need to add as a child to the child region entry
    if(inEntry.regionPath.length > regionPathIndex + 1) {
        //add as a child to the child region entry
        _addToEntry(childRegionOutEntry,inEntry,regionPathIndex+1);
    }
    else {
        //add payload data to the current out entry - but give precedence to any existing data
        if((!childRegionOutEntry.data)&&(inEntry.data)) {
            childRegionOutEntry.data = inEntry.data;
        }
        if((!childRegionOutEntry.lat)&&(inEntry.lat)) {
            childRegionOutEntry.lat = inEntry.lat;
        }
        if((!childRegionOutEntry.lon)&&(inEntry.lon)) {
            childRegionOutEntry.lon = inEntry.lon;
        }
        if((!childRegionOutEntry.fips)&&(inEntry.fips)) {
            childRegionOutEntry.fips = inEntry.fips;
        }
    }
}

