function _fillInMisingData(entry) {
    let childCount = 0;
    //initialize summed data, if needed
    let summedData;
    let dataCount = 0;
    if(entry.data === undefined) {
        summedData = _createZeroArray(dateLabels.length);
    };
    //initialize averaged lat, lon, if needed
    let summedLat, summedLon;
    let latLonCount = 0;
    if(entry.lat === undefined) {
        summedLat = 0.0;
        summedLon = 0.0;
    }

    if(entry.regions) {
        for(let childName in entry.regions) {
            //first populate the child, if needed
            let childEntry = entry.regions[childName];
            _fillInMisingData(childEntry);

            //attempt to populate this entry
            childCount++;

            if((summedData)&&(childEntry.data)) {
                _addSecondArrayToFirstArray(summedData,childEntry.data);
                dataCount++;
            }

            if((summedLat !== undefined)&&(childEntry.lat !== undefined)) {
                summedLat += childEntry.lat;
                summedLon += childEntry.lon;
                latLonCount++;
            }
        }

        //decide if we want the child data if we get entries
        //from 80 percent for more (it will probably be all or nothing)
        if((dataCount > 0)&&(dataCount > childCount * 0.8)) {
            entry.data = summedData;
        }
        //just use 50 percent for lat lon (again, it will probably be all or nothing)
        if((latLonCount > 0)&&(latLonCount > childCount * 0.5)) {
            entry.lat = summedLat / latLonCount;
            entry.lon = summedLon / latLonCount;
        }
    }
}

//this makes an array of 0s of the given length
function _createZeroArray(length) {
    let array = [];
    array.length = length;
    array.fill(0);
    return array;
}

//this modified array 1;
function _addSecondArrayToFirstArray(array1,array2) {
    array2.forEach( (value,index) => array1[index] += value);
}

