function _getRegionList(parentEntry) {
    let regionMap = parentEntry.regions;
    if(!regionMap) return null;

    let regionList = [];
    //add an "-ALL-" selection if the parent entry has its own data
    if(parentEntry.data) {
        regionList.push("-ALL-");
    }
    for(let regionName in regionMap) {
        regionList.push(regionName);
    }
    return regionList;
}

function _getDropdownLayout(selectionList,key,label) {
    return {
        type: "dropdown",
        label: label,
        entries: selectionList,
        key: key
    }
}

function _getPanelLayout(key) {
    return {
        "type":"panel",
        "formData": [
        ],
        "key": key
    }
}

/** This method gets child entry from the parent, given the region path and the start index */
function _lookupEntry(parentEntry,regionPath,pathIndex) {
    if(!parentEntry.regions) return null;

    let regionName = regionPath[pathIndex];
    let childEntry = parentEntry.regions[regionName];
    if(!childEntry) return null;

    if(pathIndex + 1 < regionPath.length) return _lookupEntry(childEntry,regionPath,pathIndex+1);
    else return childEntry;
}

