    let stateEntry = _getChildEntry(state,"state",usEntry);
    if(county) {
        return _getChildEntry(county,"county",stateEntry);
    }
    else {
        return stateEntry
    }
}

function _getChildEntry(childName,childType,parentEntry) {
    if(!parentEntry.regions) parentEntry.regions = {};

    let childEntry = parentEntry.regions[childName];
    if(!childEntry) {
        childEntry = {};
        childEntry.name = childName;
        childEntry.type = childType;
        parentEntry.regions[childName] = childEntry;
    }
    return childEntry;
}

