//make sure out format is correct
let rows = A.length;
if((A[0][0] != 0)||(A[rows-1][2] != 0)||(b.length != A.length)) throw new Error("Improper tridiag format");

//get in a solvable form
let ANew = [];
let bNew = [];
let curRow = A[0];
let curRowB = b[0];
let newRow;
let newRowB;
for(let r = 0; r < rows; r++) {
    //scale this row so the diag value is one
    let newRow = [0,1,curRow[2]/curRow[1]];
    let newRowB = curRowB/curRow[1];

    //store these values
    ANew.push(newRow);
    prevRow = newRow;
    bNew.push(newRowB);
    prevRowB = newRowB;

    //subtract current from from next soit is upper diag
    if(r < rows-1) {
        let nextRow = A[r+1];
        let nextRowB = b[r+1];

        let factor = nextRow[0];
        curRow = [0,nextRow[1] - prevRow[2]*factor,nextRow[2]];
        curRowB = nextRowB - prevRowB*factor;
    }
}

//no wwe can solve starting with the last row
xRev = [];
xRowNext = 0; //this is the x value of the next row
for(let r = rows-1; r >= 0; r--) {
    curRow = ANew[r];
    curRowB = bNew[r];
    let xRow = (curRowB - curRow[2] * xRowNext); //remember curRow[1] = 1 and curRow[0] = 0
    xRev.push(xRow);
    xRowNext = xRow;
}

return xRev.reverse();

let rows = A.length;

let b = [];
for(let r = 0; r < rows; r++) {
    let row = A[r];
    let value = 0;
    if(r > 0) value += row[0] * x[r-1];
    value += row[1] * x[r];
    if(r < rows-1) value += row[2] * x[r+1];
    b.push(value);
}

return b;
