function _deleteIncompleteEntries(entryMap) {
    for(let key in entryMap) {
        let entry = entryMap[key];
        //criteria
        //cases or deaths missing; population 0 or missing; latlon missing; lat or lon 0 or missing;
        let doDelete = ((!entry.cases)||(!entry.deaths)||(!entry.population)||(!entry.latlon)||(!entry.latlon[0])||(!entry.latlon[1])); 

        if(doDelete) delete entryMap[key];
    }
}

function _sumCountyData(countyMap) {
    let summedData = {};
    //sum/average data from the counties
    summedData.cases;
    let addToStateCases = (value,index) => { summedData.cases[index] += value; };
    summedData.deaths;
    let addToStateDeaths = (value,index) => { summedData.deaths[index] += value; };

    summedData.population = 0;
    summedData.latlon = [0,0];
    let countyCount = 0;

    for(let county in countyMap) {
        let countyEntry = countyMap[county];

        if(!summedData.cases) {
            //first pass - populate initial cases and deaths array
            summedData.cases = apogeeutil.jsonCopy(countyEntry.cases);
            summedData.deaths = apogeeutil.jsonCopy(countyEntry.deaths);
        }
        else {
            countyEntry.cases.forEach(addToStateCases);
            countyEntry.deaths.forEach(addToStateDeaths);
        }

        summedData.population += countyEntry.population;
        summedData.latlon[0] += countyEntry.latlon[0];
        summedData.latlon[1] += countyEntry.latlon[1];
        countyCount++;
    }

    if(countyCount === 0) {
        return null;
    }

    summedData.latlon[0] /= countyCount;
    summedData.latlon[1] /= countyCount;

    return summedData;
}

