    //get the change in slope between neighboring segments
    let currentSegment = lineSegmentInfo[index];
    let deltaSlopeSum = 0;
    let deltaSlopeCount = 0;
    if(index > 0) {
        let previousSegment = lineSegmentInfo[index-1];
        deltaSlopeSum += currentSegment.slope - previousSegment.slope;
        deltaSlopeCount++;
    }
    if(index < lineSegmentInfo.length-1) {
        let nextSegment = lineSegmentInfo[index+1];
        deltaSlopeSum += nextSegment.slope - currentSegment.slope;
        deltaSlopeCount++;
    }
    //average our slope changes and divide by the length for the change
    let d2ydx2 = (deltaSlopeSum/deltaSlopeCount)/N

    //get the y correction the correct for curvature
    let yCorrection = -Math.pow(N,2) * d2ydx2 / 24;
    correctionArray.push(yCorrection);
}

