let data = [];

for(let ir = 0; ir < 9; ir++) {
    data[ir] = [];
    for(let ic = 0; ic < 9; ic++) {
        let stringValue = gameBoard[ir][ic];
        if(ALLOWED_SELECTIONS.indexOf(stringValue) > -1) {
            data[ir][ic] = stringValue;
        }
        else {
            throw new Error("Invalid value in square row = " + (ir+1) + ", column = " + (ic + 1) + " (Note - sqaure value should be a string)");
        }
    }
}

return data;

let row = gameBoardData[rowIndex];
let rowSelections = [];

row.forEach( (value,index) => {
    if((index != columnIndex)&&(value !== "")) rowSelections.push(value);
});

return rowSelections;

let columnSelections = [];

gameBoardData.forEach( (row,index) => {
    let value = row[columnIndex];
    if((index != rowIndex)&&(value != "")) columnSelections.push(value)
});

return columnSelections;

let sectionSelections = [];
let iFirstRow = Math.floor(sectionNumber / 3) * 3;
let iFirstCol = (sectionNumber % 3) * 3;

for(let iSecRow = 0; iSecRow < 3; iSecRow++) {
    for(let iSecCol = 0; iSecCol < 3; iSecCol++) {

        let localSectionIndex = iSecRow*3 + iSecCol;
        if(localSectionIndex == sectionIndex) continue;

        let value = gameBoardData[iFirstRow + iSecRow][iFirstCol + iSecCol];
        if(value != "") sectionSelections.push(value);   
    }
}

return sectionSelections;

//start with all values allowed - expresssed as an array of booleans
//we will map the index of the array to selection values. Note that the
//array is 0=based while the selections are one-based.
let allowedValueBools = (new Array(9).fill(true));

//this function removes a selection from the array
let removeFromAllowedValues = selection => {
    allowedValueBools[selection-1] = false;
}

//exclude values from other sqaures in the same row
rowSelections.forEach(removeFromAllowedValues);

//exclude values from other sqaures in the same row
columnSelections.forEach(removeFromAllowedValues);

//exclude values from other sqaures in the same row
sectionSelections.forEach(removeFromAllowedValues);

//now we will convert our arry of bools to a list of selections
allowedValues = [];
allowedValueBools.forEach( (allowed,index) => {
    if(allowed) allowedValues.push(String(index+1));
});

return allowedValues;

console.log("Board analysis done!");

let squareResults = [];

for(let ir = 0; ir < 9; ir++) {
    squareResults[ir] = [];
    for(let ic = 0; ic < 9; ic++) {
        let squareSelection = gameBoardData[ir][ic];
        let squareResult = {};
        if(squareSelection == "") {
            //if there is no selection, find the allowed values
            squareResult.options = getAllowedSquareValues(ir,ic,gameBoardData);
        }
        else {
            //otherwise, show the current selection
            squareResult.selection = squareSelection;
        }

        squareResults[ir][ic] = squareResult;
    }
}

return squareResults;

let updatedSquare = [];

for(let ir = 0; ir < 9; ir++) {
    updatedSquare[ir] = [];
    for(let ic = 0; ic < 9; ic++) {
        let squareResult = boardAnalysis[ir][ic];
        if(squareResult.options) {
            //try to find a selection from the options
            let selection = NO_SELECTION_VALUE;

            //level 1 selection
            if(settings.assistLevel > 0) {
                let selection1 = getSelectionFromSingleOption(squareResult.options);
                selection = mergeSelectionValue(selection,selection1);
            }

            //level 2 selection
            if(settings.assistLevel > 1) {
                let selection2 = getSelectionFromOnlyOption(ir,ic,boardAnalysis);
                selection = mergeSelectionValue(selection,selection2);
            }

            if(selection == BAD_BOARD_VALUE) {
                //indicate an error
                apogeeMessenger.dataUpdate("gameBoardSelectionError",new Error("Bad board - illegal selection determined"));
                return;
            }
            else {
                //clear the error
                //apogeeMessenger.dataUpdate("gameBoardSelectionError","");
            }

            if(selection !== NO_SELECTION_VALUE) {
                //add the selection for this value
                updatedSquare[ir][ic] = selection;
            }
            else {
                //the square uses and empty string for no selection
                updatedSquare[ir][ic] = "";
            }
        }
        else {
            //otherwise, show the current selection
            updatedSquare[ir][ic] = squareResult.selection;
        }
    }
}


if(!apogeeutil.jsonEquals(updatedSquare,gameBoard)) {
    apogeeMessenger.dataUpdate("gameBoard",updatedSquare);
    //this entry is required for the history
    apogeeMessenger.dataUpdate("lastUpdaterResult",updatedSquare);
    return "update sent!";
}
else {
    return "no update";
}

//calculate allowed moves
let allowedSquareValuesGrid = [];

for(let ir = 0; ir < 9; ir++) {
    allowedSquareValuesGrid[ir] = [];
    for(let ic = 0; ic < 9; ic++) {
        let squareResult = boardAnalysis[ir][ic];
        if(squareResult.options) {
            //if there is no selection, find the allowed values
            allowedSquareValuesGrid[ir][ic] = squareResult.options.toString();
        }
        else {
            //otherwise, show the current selection
            allowedSquareValuesGrid[ir][ic] = `<${squareResult.selection}>`;
        }
    }
}

return allowedSquareValuesGrid;

if(squareOptions.length == 1) return squareOptions[0];
else if(squareOptions.length == 0) return BAD_BOARD_VALUE;
else return NO_SELECTION_VALUE;

let rowData = boardAnalysis[rowIndex];
let rowOptions = {};

rowData.forEach( analysisEntry => {
    if(analysisEntry.options) {
        //count the number of squares that has this as an option
        analysisEntry.options.forEach( option => {
            if(rowOptions[option]) rowOptions[option]++
            else rowOptions[option] = 1;
        })
    }
});

return rowOptions;

let columnOptions = {};

boardAnalysis.forEach( (rowData,index) => {
    let analysisEntry = rowData[columnIndex];
    if(analysisEntry.options) {
        //count the number of squares that has this as an option
        analysisEntry.options.forEach( option => {
            if(columnOptions[option]) columnOptions[option]++
            else columnOptions[option] = 1;
        })
    }
});

return columnOptions;

let sectionOptions = {};
let iFirstRow = Math.floor(sectionNumber / 3) * 3;
let iFirstCol = (sectionNumber % 3) * 3;

for(let iSecRow = 0; iSecRow < 3; iSecRow++) {
    for(let iSecCol = 0; iSecCol < 3; iSecCol++) {
        let localSectionIndex = iSecRow*3 + iSecCol;
        let analysisEntry = boardAnalysis[iFirstRow + iSecRow][iFirstCol + iSecCol];
        if(analysisEntry.options) {
            //count the number of squares that has this as an option
            analysisEntry.options.forEach( option => {
                if(sectionOptions[option]) sectionOptions[option]++
                else sectionOptions[option] = 1;
            })
        }   
    }
}

return sectionOptions;

let squareEntry = boardAnalysis[rowIndex][columnIndex];
let selection = NO_SELECTION_VALUE;
if(squareEntry.options) {
    //check if this square has an option in no other square in the row
    let rowSelection = _getSelection(squareEntry.options,rowOptions);
    selection = mergeSelectionValue(selection,rowSelection);

    //check if this square has an option in no other square in the column
    let columnSelection = _getSelection(squareEntry.options,columnOptions);
    selection = mergeSelectionValue(selection,columnSelection);

    //check if this square has an option in no other square in the section
    let sectionSelection = _getSelection(squareEntry.options,sectionOptions);
    selection = mergeSelectionValue(selection,sectionSelection);

    return selection;
}
else {
    return NO_SELECTION_VALUE;
}

updateData.supplementalCode:
function _getSelection(squareOptions,groupOptions) {
    let selection = NO_SELECTION_VALUE;
    squareOptions.forEach( squareOption => {
        let groupCount = groupOptions[squareOption];
        if(groupCount == 1) selection = mergeSelectionValue(selection,squareOption);
    })
    return selection;
}

