//if any bad board is found, keep that as the result
if((activeSelection == BAD_BOARD_VALUE)||(newSelection == BAD_BOARD_VALUE)) return BAD_BOARD_VALUE;

if(newSelection != NO_SELECTION_VALUE) {
    if(activeSelection == NO_SELECTION_VALUE) return newSelection;
    else if(activeSelection != newSelection) return BAD_BAORD_VALUE; //they should NOT disagree
    else return activeSelection; //the agree 
}
else {
    //keep the old selection
    return activeSelection;
}

let gameList = [];
for(let boardName in gameBoards) {
    gameList.push(boardName);
}

let onSubmit = formValue => {
    if(formValue.selectedGame) {
        let selectedGameBoard = gameBoards[formValue.selectedGame];
        if(selectedGameBoard) {
            //create the settings value
            let newSettings = {};
            newSettings.selectedGame = formValue.selectedGame;
            if(formValue.assistLevel !== undefined) newSettings.assistLevel = parseInt(formValue.assistLevel);
            else newSettings.assistLevel = DEFAULT_ASSIST_LEVEL;

            //we need to load the initial history too
            let initialHistory = getInitialHistory(selectedGameBoard);

            apogeeMessenger.compoundDataUpdate([["gameBoard",selectedGameBoard],["settings",newSettings],["lastUpdaterResult",""],["history",initialHistory]]);

        }
        else {
            alert("Error reading game board! Selection not made");
        }
    }
    else {
        alert("A game board must be selected")
    }
}

return [
    {
        type: "dropdown",
        label: "Select Game Board: ",
        entries: gameList,
        value: (settings.selectedGame !== undefined) ? settings.selectedGame : DEFAULT_GAME_NAME,
        key: "selectedGame"
    },
    {
        type: "radioButtonGroup",
        label: "Auto select: ",
        entries: [["Level 0",0],["Level 1",1],["Level 2",2]],
        value: (settings.assistLevel !== undefined) ? settings.assistLevel : DEFAULT_ASSIST_LEVEL,
        horizontal: true,
        key: "assistLevel"
    },
    {
        type: "submit",
        submitLabel: "Start New Game",
        onSubmit: onSubmit
    }
];

//add to the history, if this is not from the updater
console.log("In history updater");
if(history.currentEntry >= 0) {
    let latestBoardIsInHistory = apogeeutil.jsonEquals(history.boards[history.currentEntry],gameBoard);
    if(latestBoardIsInHistory) {
        console.log("History updater: history up to date");
        return "up to date";
    }
}


let latestBoardIsFromUpdater = apogeeutil.jsonEquals(gameBoard,lastUpdaterResult);
if(!latestBoardIsFromUpdater) {
    addToHistory(gameBoard,apogeeMessenger);
    console.log("History updater: new gameboard saved");
    return "Selection from player";
}
else {
    console.log("History updater: update from updated. No history entry made");
    return "Selection from updater";
}

//the revert available function takes a gratuitous instance of history. 
//this is because we need this cell to depend on history, so the button status updates.
let historyForRevert = history;
let onSubmit = () => revertFromHistory(apogeeMessenger);

return [
    {
        type: "submit",
        submitLabel: "Revert Last Move",
        onSubmit: onSubmit,
        submitDisabled: !revertAvailable(historyForRevert)
    }
];

if(history.currentEntry > 0) {
    let previousGameBoard = history.boards[history.currentEntry-1];
    let newHistory = {};
    newHistory.currentEntry = history.currentEntry-1;
    newHistory.boards = history.boards;

    //set the game board and the clear last updater result
    //also update the history
    messenger.compoundDataUpdate([["gameBoard",previousGameBoard],["lastUpdaterResult",""],["history",newHistory]]);
}

let oldBoards;
if(history.currentEntry < history.boards.length-1) {
    oldBoards = history.boards.slice(0,history.currentEntry+1);
}
else {
    oldBoards = history.boards;
}

let newHistory = {};
newHistory.boards = oldBoards.concat([gameBoard]);
newHistory.currentEntry = history.currentEntry+1;

messenger.dataUpdate("history",newHistory);

let newHistory = {};
newHistory.boards = [firstBoard];
newHistory.currentEntry = 0;

return newHistory;
