updateData.functionBody:
var onSubmit = data => {
    apogeeMessenger.dataUpdate("output",data);
}

//set values
var textFieldValue = ((output)&&(output.name !== undefined)) ? output.name : "";
var radioGroupValue = ((output)&&(output.favAnimal !== undefined)) ? output.favAnimal : "antelope";

return [
	{
		"type": "textField",
		"label": "Name: ",
		"value": textFieldValue,
		"key": "name"
	},
	{
		"type": "radioButtonGroup",
		"label": "Favorite Animal: ",
		"groupName": "favAnimalGroup",
		"entries": [
			"antelope",
			"buffalo",
			"cat",
			"dog"
		],
		"value": radioGroupValue,
		"key": "favAnimal"
	},
	{
	    "type":"submit",
	    "onSubmit": onSubmit
	}
];
