updateData.supplementalCode:
function createEntry(year,revenue) {
    entry = {};
    entry.year = year;
    entry.revenue = revenue;
    return entry;
}

function getNextEntry(entry) {
    var nextEntry = {};
    nextEntry.year = entry.year + 1;
    nextEntry.revenue = entry.revenue * (1 + settings.growthRate/100);
    return nextEntry;
}
